import React, { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { X, FileText, Edit } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';

interface CreativeWritingPopupProps {
  isOpen: boolean;
  onClose: () => void;
  contentTitle?: string;
  studentId: string;
  contentId?: string;
  outlineData: {
    title: string;
    directions: string;
    setting: string;
    characters: string;
    first: string;
    andThen1: string;
    andThen2: string;
    andFinally: string;
  };
  onBackToOutline?: () => void;
}

interface WritingData {
  title: string;
  story: string;
}

export default function CreativeWritingPopup({ 
  isOpen, 
  onClose, 
  contentTitle, 
  studentId, 
  contentId,
  outlineData,
  onBackToOutline 
}: CreativeWritingPopupProps) {
  const [writingData, setWritingData] = useState<WritingData>({
    title: outlineData.title || '',
    story: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();

  // Load saved data on open
  useEffect(() => {
    if (isOpen && studentId && contentId) {
      const storageKey = `creative_story_${studentId}_${contentId}`;
      const savedData = localStorage.getItem(storageKey);
      if (savedData) {
        try {
          const parsed = JSON.parse(savedData);
          setWritingData({
            title: parsed.title || outlineData.title || '',
            story: parsed.story || ''
          });
        } catch (error) {
          console.error('Failed to parse saved story data:', error);
          setWritingData({
            title: outlineData.title || '',
            story: ''
          });
        }
      } else {
        // Reset to initial state for new content
        setWritingData({
          title: outlineData.title || '',
          story: ''
        });
      }
    }
  }, [isOpen, studentId, contentId, outlineData]);

  // Save data when popup closes or story changes
  useEffect(() => {
    if (studentId && contentId && (writingData.title.trim() || writingData.story.trim())) {
      const storageKey = `creative_story_${studentId}_${contentId}`;
      localStorage.setItem(storageKey, JSON.stringify(writingData));
    }
  }, [writingData, studentId, contentId]);

  // Save data when browser closes
  useEffect(() => {
    const saveOnUnload = () => {
      if (studentId && contentId && (writingData.title.trim() || writingData.story.trim())) {
        const storageKey = `creative_story_${studentId}_${contentId}`;
        localStorage.setItem(storageKey, JSON.stringify(writingData));
      }
    };

    window.addEventListener('beforeunload', saveOnUnload);
    return () => window.removeEventListener('beforeunload', saveOnUnload);
  }, [writingData, studentId, contentId]);

  const handleStoryChange = (value: string) => {
    setWritingData(prev => ({ ...prev, story: value }));
  };

  const handleSectionChange = (sectionIndex: number, value: string) => {
    const paragraphs = writingData.story.split('\n\n');
    paragraphs[sectionIndex] = value;
    setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
  };

  const getWordCount = (text: string) => {
    return text.trim() ? text.trim().split(/\s+/).length : 0;
  };

  const submitStory = async () => {
    if (!studentId || !contentId) {
      toast({
        title: "Submission Failed",
        description: "Missing student or content information.",
        variant: "destructive",
      });
      return;
    }

    const storyWordCount = getWordCount(writingData.story);
    if (storyWordCount < 50) {
      toast({
        title: "Submission Failed",
        description: "Story must be at least 50 words to submit.",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);
    try {
      // Split story into paragraphs for database storage
      const paragraphs = writingData.story.split('\n\n').filter(p => p.trim());

      const response = await fetch('/api/writing-submissions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          student_id: studentId,
          content_id: contentId,
          content_title: contentTitle,
          outline_data: outlineData,
          essay_data: {
            introduction: paragraphs[0] || '',
            body1: paragraphs[1] || '',
            body2: paragraphs[2] || '',
            body3: paragraphs[3] || '',
            conclusion: paragraphs[4] || ''
          },
          word_count: storyWordCount,
          submitted_at: new Date().toISOString()
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to submit story');
      }

      const result = await response.json();
      console.log('Writing submission created successfully:', result);

      // Clear both story and outline data from localStorage after successful submission
      if (studentId && contentId) {
        const storyStorageKey = `creative_story_${studentId}_${contentId}`;
        const outlineStorageKey = `creative_outline_${studentId}_${contentId}`;
        localStorage.removeItem(storyStorageKey);
        localStorage.removeItem(outlineStorageKey);
      }

      toast({
        title: "Story Submitted",
        description: `Your creative writing has been submitted successfully (${storyWordCount} words).`,
      });

      onClose();
      setWritingData({ title: '', story: '' });
    } catch (error) {
      console.error('Submit error:', error);
      toast({
        title: "Submission Failed",
        description: error instanceof Error ? error.message : "There was an error submitting your story. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <div>
              <DialogTitle className="text-xl font-bold">Creative Writing</DialogTitle>
              {contentTitle && (
                <div className="mt-2 p-3 bg-gray-50 rounded-lg border-l-4 border-green-500">
                  <p className="text-lg font-semibold text-gray-800">{contentTitle}</p>
                </div>
              )}
            </div>
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        <div className="space-y-2 p-2">
          {/* Outline Summary - Similar to Academic Essay */}
          <div className="bg-blue-50 p-3 rounded-lg border">
            <h3 className="text-lg font-semibold mb-2 flex items-center text-blue-800">
              <FileText className="h-5 w-5 mr-2" />
              Your Creative Writing Outline
            </h3>

            {/* Title and Directions */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 mb-3">
              {outlineData.title && (
                <div className="bg-blue-100 px-2 py-1 rounded-md border-l-4 border-blue-400">
                  <span className="text-sm font-bold text-blue-800">Title:</span>
                  <span className="text-sm text-blue-700 ml-2">{outlineData.title}</span>
                </div>
              )}
              {outlineData.directions && (
                <div className="bg-blue-100 px-2 py-1 rounded-md border-l-4 border-blue-400">
                  <span className="text-sm font-bold text-blue-800">Directions:</span>
                  <span className="text-sm text-blue-700 ml-2">{outlineData.directions}</span>
                </div>
              )}
            </div>

            {/* Setting and Characters */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 mb-3">
              {outlineData.setting && (
                <div className="bg-green-100 px-2 py-1 rounded-md border-l-4 border-green-400">
                  <span className="text-sm font-bold text-green-800">Setting:</span>
                  <span className="text-sm text-green-700 ml-2">{outlineData.setting}</span>
                </div>
              )}
              {outlineData.characters && (
                <div className="bg-green-100 px-2 py-1 rounded-md border-l-4 border-green-400">
                  <span className="text-sm font-bold text-green-800">Characters:</span>
                  <span className="text-sm text-green-700 ml-2">{outlineData.characters}</span>
                </div>
              )}
            </div>

            {/* Story Structure */}
            <div>
              <span className="text-sm font-bold text-purple-800">Story Structure:</span>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-2 mt-2">
                {outlineData.first && (
                  <div className="bg-purple-100 px-2 py-1 rounded-md border-l-4 border-purple-400">
                    <span className="text-sm font-bold text-purple-800">First:</span>
                    <span className="text-sm text-purple-700 ml-2">{outlineData.first}</span>
                  </div>
                )}
                {outlineData.andThen1 && (
                  <div className="bg-purple-100 px-2 py-1 rounded-md border-l-4 border-purple-400">
                    <span className="text-sm font-bold text-purple-800">Then:</span>
                    <span className="text-sm text-purple-700 ml-2">{outlineData.andThen1}</span>
                  </div>
                )}
                {outlineData.andThen2 && (
                  <div className="bg-purple-100 px-2 py-1 rounded-md border-l-4 border-purple-400">
                    <span className="text-sm font-bold text-purple-800">Then:</span>
                    <span className="text-sm text-purple-700 ml-2">{outlineData.andThen2}</span>
                  </div>
                )}
                {outlineData.andFinally && (
                  <div className="bg-purple-100 px-2 py-1 rounded-md border-l-4 border-purple-400">
                    <span className="text-sm font-bold text-purple-800">Finally:</span>
                    <span className="text-sm text-purple-700 ml-2">{outlineData.andFinally}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Title */}
          <div className="bg-blue-50 p-3 rounded-lg border">
            <div className="flex justify-between items-center mb-1">
              <h4 className="font-semibold text-blue-800">Story Title</h4>
            </div>
            <input
              type="text"
              value={writingData.title}
              onChange={(e) => setWritingData(prev => ({ ...prev, title: e.target.value }))}
              className="w-full p-2 border rounded border-blue-200"
              placeholder="Enter your story title..."
            />
          </div>

          {/* Writing sections similar to academic essay */}
          <div className="space-y-2">
            {/* Opening */}
            <div className="bg-blue-50 p-3 rounded-lg border">
              <div className="flex justify-between items-center mb-1">
                <div className="flex items-center gap-3">
                  <h4 className="font-semibold text-blue-800">Opening</h4>
                  {outlineData.first && (
                    <div className="bg-blue-100 px-2 py-1 rounded-md border-l-4 border-blue-400">
                      <p className="text-sm text-blue-700">{outlineData.first}</p>
                    </div>
                  )}
                </div>
                <Button variant="ghost" size="sm" className="text-xs">
                  {getWordCount(writingData.story.split('\n\n')[0] || '')} words
                </Button>
              </div>
              <Textarea
                placeholder="Begin your story with an engaging opening scene..."
                value={writingData.story.split('\n\n')[0] || ''}
                onChange={(e) => {
                  const paragraphs = writingData.story.split('\n\n');
                  paragraphs[0] = e.target.value;
                  setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
                }}
                className="min-h-[100px] border-blue-200 w-full"
              />
            </div>

            {/* Body 1 */}
            <div className="bg-green-50 p-3 rounded-lg border">
              <div className="flex justify-between items-center mb-1">
                <div className="flex items-center gap-3">
                  <h4 className="font-semibold text-green-800">Body 1</h4>
                  {outlineData.andThen1 && (
                    <div className="bg-green-100 px-2 py-1 rounded-md border-l-4 border-green-400">
                      <p className="text-sm text-green-700">{outlineData.andThen1}</p>
                    </div>
                  )}
                </div>
                <Button variant="ghost" size="sm" className="text-xs">
                  {getWordCount(writingData.story.split('\n\n')[1] || '')} words
                </Button>
              </div>
              <Textarea
                placeholder="Develop your first main story point..."
                value={writingData.story.split('\n\n')[1] || ''}
                onChange={(e) => {
                  const paragraphs = writingData.story.split('\n\n');
                  paragraphs[1] = e.target.value;
                  setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
                }}
                className="min-h-[120px] border-green-200 w-full"
              />
            </div>

            {/* Body 2 */}
            <div className="bg-green-50 p-3 rounded-lg border">
              <div className="flex justify-between items-center mb-1">
                <div className="flex items-center gap-3">
                  <h4 className="font-semibold text-green-800">Body 2</h4>
                  {outlineData.andThen2 && (
                    <div className="bg-green-100 px-2 py-1 rounded-md border-l-4 border-green-400">
                      <p className="text-sm text-green-700">{outlineData.andThen2}</p>
                    </div>
                  )}
                </div>
                <Button variant="ghost" size="sm" className="text-xs">
                  {getWordCount(writingData.story.split('\n\n')[2] || '')} words
                </Button>
              </div>
              <Textarea
                placeholder="Continue developing your story..."
                value={writingData.story.split('\n\n')[2] || ''}
                onChange={(e) => {
                  const paragraphs = writingData.story.split('\n\n');
                  paragraphs[2] = e.target.value;
                  setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
                }}
                className="min-h-[120px] border-green-200 w-full"
              />
            </div>

            {/* Body 3 */}
            <div className="bg-green-50 p-3 rounded-lg border">
              <div className="flex justify-between items-center mb-1">
                <div className="flex items-center gap-3">
                  <h4 className="font-semibold text-green-800">Body 3</h4>
                </div>
                <Button variant="ghost" size="sm" className="text-xs">
                  {getWordCount(writingData.story.split('\n\n')[3] || '')} words
                </Button>
              </div>
              <Textarea
                placeholder="Build towards the climax of your story..."
                value={writingData.story.split('\n\n')[3] || ''}
                onChange={(e) => {
                  const paragraphs = writingData.story.split('\n\n');
                  paragraphs[3] = e.target.value;
                  setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
                }}
                className="min-h-[120px] border-green-200 w-full"
              />
            </div>

            {/* Conclusion */}
            <div className="bg-purple-50 p-3 rounded-lg border">
              <div className="flex justify-between items-center mb-1">
                <div className="flex items-center gap-3">
                  <h4 className="font-semibold text-purple-800">Conclusion</h4>
                  {outlineData.andFinally && (
                    <div className="bg-purple-100 px-2 py-1 rounded-md border-l-4 border-purple-400">
                      <p className="text-sm text-purple-700">{outlineData.andFinally}</p>
                    </div>
                  )}
                </div>
                <Button variant="ghost" size="sm" className="text-xs">
                  {getWordCount(writingData.story.split('\n\n')[4] || '')} words
                </Button>
              </div>
              <Textarea
                placeholder="Conclude your story with a satisfying ending..."
                value={writingData.story.split('\n\n')[4] || ''}
                onChange={(e) => {
                  const paragraphs = writingData.story.split('\n\n');
                  paragraphs[4] = e.target.value;
                  setWritingData(prev => ({ ...prev, story: paragraphs.join('\n\n') }));
                }}
                className="min-h-[100px] border-purple-200 w-full"
              />
            </div>
          </div>

          {/* Action Buttons */}
          <div className="bg-gray-50 p-2 rounded-lg">
            <div className="flex justify-between items-center">
              <div>
                <span className="text-lg font-semibold">Total: {getWordCount(writingData.story)} words</span>
                <p className="text-sm text-gray-600">Continue developing your creative story</p>
              </div>

              <div className="flex gap-2">
                {onBackToOutline && (
                  <Button variant="outline" onClick={onBackToOutline} className="border-purple-500 text-purple-600 hover:bg-purple-50">
                    <Edit className="h-4 w-4 mr-2" />
                    Back to Outline
                  </Button>
                )}
                <Button variant="outline" onClick={onClose}>
                  Save Draft
                </Button>
                <Button 
                  onClick={submitStory}
                  disabled={isSubmitting || !writingData.title.trim() || !writingData.story.trim() || getWordCount(writingData.story) < 50}
                  className="bg-green-600 hover:bg-green-700"
                >
                  {isSubmitting ? 'Submitting...' : 'Submit Story'}
                  <FileText className="h-4 w-4 ml-2" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/EnhancedContentPro.content:
import React, { useState, useMemo } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { BarChart3, ChevronDown, ChevronRight, FolderOpen, Folder, FileText, Users, Filter, Eye } from "lucide-react";
import ContentPopup from "./ContentPopup";
import type { Topic, Content, ContentRating, User } from "@shared/schema";

interface HierarchyItem {
  id: string;
  title: string;
  type: 'topic' | 'subtopic' | 'groupcard' | 'content';
  rating?: 'ok' | 'normal' | 'really_bad' | null;
  children: HierarchyItem[];
  contentData?: Content;
  parentid?: string | null;
  isExpanded?: boolean;
}

interface Student {
  id: string;
  full_name: string;
  first_name?: string;
  last_name?: string;
}

export const EnhancedContentProgressPanel = () => {
  const [activeTab, setActiveTab] = useState<'student' | 'teacher'>('student');
  const [filterRating, setFilterRating] = useState<'all' | 'ok' | 'really_bad'>('all');
  const [selectedStudent, setSelectedStudent] = useState<string>('GV0002');
  const [selectedContent, setSelectedContent] = useState<Content | null>(null);
  const [isContentPopupOpen, setIsContentPopupOpen] = useState(false);
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());

  const queryClient = useQueryClient();

  // Fetch topics
  const { data: topics, isLoading: topicsLoading } = useQuery({
    queryKey: ['/api/topics'],
    queryFn: async (): Promise<Topic[]> => {
      const response = await fetch('/api/topics');
      if (!response.ok) throw new Error('Failed to fetch topics');
      return response.json();
    },
  });

  // Fetch content
  const { data: content, isLoading: contentLoading } = useQuery({
    queryKey: ['/api/content'],
    queryFn: async (): Promise<Content[]> => {
      const response = await fetch('/api/content');
      if (!response.ok) throw new Error('Failed to fetch content');
      return response.json();
    },
  });

  // Fetch content ratings for selected student
  const { data: contentRatings, isLoading: ratingsLoading } = useQuery({
    queryKey: ['/api/content-ratings', selectedStudent],
    queryFn: async (): Promise<ContentRating[]> => {
      const response = await fetch(`/api/content-ratings/${selectedStudent}`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedStudent,
  });

  // For teacher view, fetch all students
  const { data: students } = useQuery({
    queryKey: ['/api/students'],
    queryFn: async (): Promise<Student[]> => {
      const response = await fetch('/api/users');
      if (!response.ok) throw new Error('Failed to fetch students');
      const users = await response.json();
      return users.map((user: any) => ({
        id: user.id,
        full_name: user.full_name || (user.first_name && user.last_name ? `${user.first_name} ${user.last_name}` : user.id),
        first_name: user.first_name,
        last_name: user.last_name
      }));
    },
    enabled: activeTab === 'teacher',
  });

  // Build hierarchy
  const hierarchy = useMemo(() => {
    if (!topics || !content) return [];

    const ratingMap = new Map<string, ContentRating>();
    contentRatings?.forEach(rating => {
      ratingMap.set(rating.content_id, rating);
    });

    // Build topic hierarchy
    const topicMap = new Map<string, Topic>();
    topics.forEach(topic => {
      topicMap.set(topic.id, topic);
    });

    const buildTopicHierarchy = (parentId: string | null): HierarchyItem[] => {
      return topics
        .filter(topic => topic.parentid === parentId)
        .map(topic => {
          const topicContent = content.filter(c => c.topicid === topic.id);
          const children: HierarchyItem[] = [];

          // Add subtopics
          children.push(...buildTopicHierarchy(topic.id));

          // Group content by prompt (for grouped content cards)
          const groupedContent = new Map<string, Content[]>();
          const ungroupedContent: Content[] = [];

          topicContent.forEach(c => {
            if (c.prompt === 'groupcard') {
              const key = c.parentid || 'default';
              if (!groupedContent.has(key)) {
                groupedContent.set(key, []);
              }
              groupedContent.get(key)!.push(c);
            } else if (c.prompt !== 'groupcard') {
              ungroupedContent.push(c);
            }
          });

          // Add grouped content cards
          groupedContent.forEach((groupContents, groupKey) => {
            const groupParent = groupContents.find(c => c.parentid === null);
            if (groupParent) {
              const groupChildren = groupContents
                .filter(c => c.parentid !== null)
                .map(c => ({
                  id: c.id,
                  title: c.title || c.short_description || 'Untitled',
                  type: 'content' as const,
                  rating: (ratingMap.get(c.id)?.rating as 'ok' | 'normal' | 'really_bad') || null,
                  children: [],
                  contentData: c,
                }));

              children.push({
                id: groupParent.id,
                title: groupParent.title || groupParent.short_description || 'Group Content',
                type: 'groupcard',
                rating: (ratingMap.get(groupParent.id)?.rating as 'ok' | 'normal' | 'really_bad') || null,
                children: groupChildren,
                contentData: groupParent,
              });
            }
          });

          // Add ungrouped content
          ungroupedContent.forEach(c => {
            children.push({
              id: c.id,
              title: c.title || c.short_description || 'Untitled',
              type: 'content',
              rating: (ratingMap.get(c.id)?.rating as 'ok' | 'normal' | 'really_bad') || null,
              children: [],
              contentData: c,
            });
          });

          return {
            id: topic.id,
            title: topic.topic || 'Untitled Topic',
            type: topic.parentid ? 'subtopic' : 'topic',
            children,
          } as HierarchyItem;
        });
    };

    return buildTopicHierarchy(null);
  }, [topics, content, contentRatings]);

  // Filter hierarchy by rating
  const filteredHierarchy = useMemo(() => {
    if (filterRating === 'all') return hierarchy;

    const filterNode = (node: HierarchyItem): HierarchyItem | null => {
      const filteredChildren = node.children
        .map(child => filterNode(child))
        .filter(Boolean) as HierarchyItem[];

      const hasMatchingRating = node.rating === filterRating;
      const hasMatchingChildren = filteredChildren.length > 0;

      if (hasMatchingRating || hasMatchingChildren) {
        return {
          ...node,
          children: filteredChildren,
        };
      }

      return null;
    };

    return hierarchy
      .map(node => filterNode(node))
      .filter(Boolean) as HierarchyItem[];
  }, [hierarchy, filterRating]);

  const getRatingColor = (rating: string | null) => {
    switch (rating) {
      case 'ok': return 'bg-green-500 text-white';
      case 'really_bad': return 'bg-red-500 text-white';
      case 'normal': return 'bg-yellow-500 text-white';
      default: return 'bg-gray-400 text-white';
    }
  };

  const getRatingLabel = (rating: string | null) => {
    switch (rating) {
      case 'ok': return 'Easy';
      case 'really_bad': return 'Hard';
      case 'normal': return 'Normal';
      default: return 'Unrated';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'topic': return <Folder className="h-4 w-4" />;
      case 'subtopic': return <FolderOpen className="h-4 w-4" />;
      case 'groupcard': return <Folder className="h-4 w-4 text-blue-400" />;
      case 'content': return <FileText className="h-4 w-4" />;
      default: return <FileText className="h-4 w-4" />;
    }
  };

  const toggleExpansion = (itemId: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);
  };

  const handleContentClick = (content: Content) => {
    setSelectedContent(content);
    setIsContentPopupOpen(true);
  };

  const renderHierarchyItem = (item: HierarchyItem, depth: number = 0) => {
    const isExpanded = expandedItems.has(item.id);
    const hasChildren = item.children.length > 0;
    const paddingLeft = depth * 16;

    return (
      <div key={item.id} className="w-full">
        <div 
          className="flex items-center gap-2 p-2 hover:bg-gray-50 dark:hover:bg-gray-700 rounded cursor-pointer"
          style={{ paddingLeft: `${paddingLeft + 8}px` }}
        >
          {hasChildren && (
            <Button
              variant="ghost"
              size="sm"
              className="h-4 w-4 p-0"
              onClick={() => toggleExpansion(item.id)}
            >
              {isExpanded ? <ChevronDown className="h-3 w-3" /> : <ChevronRight className="h-3 w-3" />}
            </Button>
          )}
          {!hasChildren && <div className="w-4" />}

          {getTypeIcon(item.type)}

          <span 
            className="flex-1 text-sm font-medium truncate"
            onClick={() => item.contentData && handleContentClick(item.contentData)}
          >
            {item.title}
          </span>

          {item.rating && (
            <Badge className={`text-xs h-5 ${getRatingColor(item.rating)}`}>
              {getRatingLabel(item.rating)}
            </Badge>
          )}

          {item.contentData && (
            <Button
              variant="ghost"
              size="sm"
              className="h-6 w-6 p-0"
              onClick={() => handleContentClick(item.contentData!)}
            >
              <Eye className="h-3 w-3" />
            </Button>
          )}
        </div>

        {hasChildren && isExpanded && (
          <div className="ml-2">
            {item.children.map(child => renderHierarchyItem(child, depth + 1))}
          </div>
        )}
      </div>
    );
  };

  const getStats = () => {
    const allRatings = contentRatings || [];
    return {
      total: allRatings.length,
      ok: allRatings.filter(r => r.rating === 'ok').length,
      really_bad: allRatings.filter(r => r.rating === 'really_bad').length,
      normal: allRatings.filter(r => r.rating === 'normal').length,
    };
  };

  const stats = getStats();
  const isLoading = topicsLoading || contentLoading || ratingsLoading;

  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button 
          variant="outline" 
          size="sm"
          className="bg-white/10 border-white/20 text-white/80 hover:bg-white/20 hover:text-white transition-all duration-300 h-8 w-8 p-0"
          title="Enhanced Content Progress"
        >
          <BarChart3 className="h-4 w-4" />
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden bg-white dark:bg-gray-900 border-gray-200 dark:border-gray-700">
        <DialogHeader>
          <DialogTitle className="text-gray-900 dark:text-white flex items-center gap-2">
            <BarChart3 className="h-5 w-5 text-blue-500" />
            Enhanced Content Progress Directory
          </DialogTitle>
          <DialogDescription className="text-gray-600 dark:text-gray-400">
            Hierarchical view of content with ratings and progress tracking
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as 'student' | 'teacher')} className="w-full">
          <TabsList className="grid w-full grid-cols-2 bg-gray-100 dark:bg-gray-800">
            <TabsTrigger value="student" className="flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Student View
            </TabsTrigger>
            <TabsTrigger value="teacher" className="flex items-center gap-2">
              <Users className="h-4 w-4" />
              Teacher Dashboard
            </TabsTrigger>
          </TabsList>

          <TabsContent value="student" className="space-y-4 mt-4">
            {/* Student Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-2">
              <Card className="bg-gray-50 dark:bg-gray-800 border-gray-200 dark:border-gray-700 p-2">
                <div className="text-center">
                  <div className="text-xl font-bold text-gray-900 dark:text-white">{stats.total}</div>
                  <div className="text-xs text-gray-600 dark:text-gray-400">Total Rated</div>
                </div>
              </Card>
              <Card className="bg-gray-50 dark:bg-gray-800 border-gray-200 dark:border-gray-700 p-2">
                <div className="text-center">
                  <div className="text-xl font-bold text-green-600">{stats.ok}</div>
                  <div className="text-xs text-gray-600 dark:text-gray-400">Easy</div>
                </div>
              </Card>
              <Card className="bg-gray-50 dark:bg-gray-800 border-gray-200 dark:border-gray-700 p-2">
                <div className="text-center">
                  <div className="text-xl font-bold text-red-600">{stats.really_bad}</div>
                  <div className="text-xs text-gray-600 dark:text-gray-400">Hard</div>
                </div>
              </Card>
              <Card className="bg-gray-50 dark:bg-gray-800 border-gray-200 dark:border-gray-700 p-2">
                <div className="text-center">
                  <div className="text-xl font-bold text-yellow-600">{stats.normal}</div>
                  <div className="text-xs text-gray-600 dark:text-gray-400">Normal</div>
                </div>
              </Card>
            </div>

            {/* Filter Controls */}
            <div className="flex items-center gap-2">
              <Filter className="h-4 w-4 text-gray-600 dark:text-gray-400" />
              <Select value={filterRating} onValueChange={(value) => setFilterRating(value as any)}>
                <SelectTrigger className="w-40 bg-white dark:bg-gray-800">
                  <SelectValue placeholder="Filter by rating" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Ratings</SelectItem>
                  <SelectItem value="ok">Easy Only</SelectItem>
                  <SelectItem value="really_bad">Hard Only</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Hierarchical Content Tree */}
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <CardHeader className="pb-2">
                <CardTitle className="text-lg text-gray-900 dark:text-white">Content Directory</CardTitle>
              </CardHeader>
              <CardContent className="p-0">
                <div className="max-h-96 overflow-y-auto">
                  {isLoading ? (
                    <div className="space-y-2 p-4">
                      {[...Array(8)].map((_, i) => (
                        <Skeleton key={i} className="h-8 bg-gray-200 dark:bg-gray-700" />
                      ))}
                    </div>
                  ) : filteredHierarchy.length === 0 ? (
                    <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                      <BarChart3 className="h-12 w-12 mx-auto mb-3 text-gray-400" />
                      <p>No content found with selected filters</p>
                    </div>
                  ) : (
                    <div className="p-2">
                      {filteredHierarchy.map(item => renderHierarchyItem(item))}
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="teacher" className="space-y-4 mt-4">
            {/* Student Selection */}
            <div className="flex items-center gap-2">
              <Users className="h-4 w-4 text-gray-600 dark:text-gray-400" />
              <Select value={selectedStudent} onValueChange={setSelectedStudent}>
                <SelectTrigger className="w-60 bg-white dark:bg-gray-800">
                  <SelectValue placeholder="Select student" />
                </SelectTrigger>
                <SelectContent>
                  {students?.map((student) => (
                    <SelectItem key={student.id} value={student.id}>
                      {student.full_name} ({student.id})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Teacher view uses same hierarchy as student view but for selected student */}
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <CardHeader className="pb-2">
                <CardTitle className="text-lg text-gray-900 dark:text-white">
                  {selectedStudent}'s Content Progress
                </CardTitle>
              </CardHeader>
              <CardContent className="p-0">
                <div className="max-h-96 overflow-y-auto">
                  {isLoading ? (
                    <div className="space-y-2 p-4">
                      {[...Array(8)].map((_, i) => (
                        <Skeleton key={i} className="h-8 bg-gray-200 dark:bg-gray-700" />
                      ))}
                    </div>
                  ) : filteredHierarchy.length === 0 ? (
                    <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                      <BarChart3 className="h-12 w-12 mx-auto mb-3 text-gray-400" />
                      <p>No ratings found for this student</p>
                    </div>
                  ) : (
                    <div className="p-2">
                      {filteredHierarchy.map(item => renderHierarchyItem(item))}
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>

      {/* Content Popup */}
      {selectedContent && (
        <ContentPopup
          isOpen={isContentPopupOpen}
          onClose={() => setIsContentPopupOpen(false)}
          content={selectedContent}
          contentList={content || []}
          onContentChange={(newContent) => setSelectedContent(newContent)}
          imageUrl={null}
          isImageLoading={false}
        />
      )}
    </Dialog>
  );
};

export default EnhancedContentProgressPanel;

client/src/components/GroupedContentCard.content:
import React, { useState } from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ChevronDown, ChevronRight, BookOpen, Play, Folder, HelpCircle, Shuffle } from "lucide-react";
import { cn } from "@/lib/utils";
import { Content } from "@/hooks/useContent";
import { useContentImage } from "@/hooks/useContentImage";
import { CompactContentDifficultyIndicator } from "@/components/ContentDifficultyIndicator";
import { ContentRatingButtons } from "@/components/ContentRatingButtons";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";

// Thumbnail component for gallery images
interface ThumbnailImageProps {
  content: Content;
  onContentClick: (info: { content: Content; contextList: Content[] }) => void;
  contextList: Content[];
}

const ThumbnailImageComponent: React.FC<ThumbnailImageProps> = ({ content, onContentClick, contextList }) => {
  const { data: thumbUrl } = useContentImage(content.imageid);

  if (!thumbUrl) return null;

  return (
    <div 
      className="w-12 h-12 rounded-md overflow-hidden flex-shrink-0 cursor-pointer hover:opacity-80 transition-opacity"
      onClick={(e) => {
        e.stopPropagation();
        onContentClick({ content, contextList });
      }}
    >
      <img 
        src={thumbUrl} 
        alt={content.title || 'Content'} 
        className="w-full h-full object-cover"
      />
    </div>
  );
};

interface GroupedContentCardProps {
  groupContent: Content; // The main content item where prompt = "groupcard"
  groupedContent: Content[]; // Related content items where contentgroup = groupContent.id
  onContentClick: (info: { content: Content; contextList: Content[] }) => void;
  onStartQuiz: (content: Content, contextList: Content[], level: 'Easy' | 'Hard') => void;
  className?: string;
  activeContentId?: string | null;
}

export const GroupedContentCard: React.FC<GroupedContentCardProps> = ({
  groupContent,
  groupedContent,
  onContentClick,
  onStartQuiz,
  className,
  activeContentId
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { data: groupImageUrl } = useContentImage(groupContent.imageid);

  const toggleExpanded = () => {
    setIsExpanded(prev => !prev);
  };

  const handleGroupContentClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    onContentClick({ content: groupContent, contextList: [groupContent, ...groupedContent] });
  };

  const handleNestedContentClick = (content: Content, e: React.MouseEvent) => {
    e.stopPropagation();
    onContentClick({ content, contextList: [groupContent, ...groupedContent] });
  };

  return (
    <Card 
      className={cn(
        "bg-gradient-to-r from-purple-600/20 via-blue-600/20 to-indigo-600/20 border-purple-400/30 hover:from-purple-600/30 hover:via-blue-600/30 hover:to-indigo-600/30 transition-all duration-200 backdrop-blur-sm",
        activeContentId === groupContent.id && "ring-4 ring-yellow-400/80 bg-yellow-500/20 border-yellow-400/70 shadow-lg shadow-yellow-400/20",
        className
      )}
    >
      <CardContent className="p-4">
        {/* Group Header with title, buttons, and expand/collapse */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-3">
            <div className="flex-shrink-0">
              <Folder className="h-5 w-5 text-purple-300" />
            </div>

            {/* Main thumbnail - showing full picture with object-contain */}
            {groupImageUrl && (
              <div 
                className="w-20 h-16 rounded-md overflow-hidden flex-shrink-0 cursor-pointer hover:opacity-80 transition-opacity"
                onClick={handleGroupContentClick}
              >
                <img 
                  src={groupImageUrl} 
                  alt={groupContent.title || 'Group content'} 
                  className="w-full h-full object-contain"
                />
              </div>
            )}
          </div>

          {/* Centered Title */}
          <div className="flex-1 text-center mx-4">
            <h3 
              className="font-semibold text-lg line-clamp-1 cursor-pointer hover:opacity-90"
              onClick={handleGroupContentClick}
              style={{ color: 'white !important', textDecoration: 'none' }}
            >
              {groupContent.title || groupContent.short_description || 'Grouped Content'}
            </h3>
          </div>

          {/* Quiz and Match buttons - compact and stacked vertically */}
          <div className="flex flex-col gap-1 mr-3">
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button
                  size="sm"
                  variant="outline"
                  className="h-6 px-2 text-xs bg-gray-700/50 border-gray-600 text-gray-300 hover:bg-gray-600/50"
                  onClick={(e) => e.stopPropagation()}
                >
                  <HelpCircle className="h-3 w-3 mr-1" />
                  Quiz
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent>
                <DropdownMenuItem onClick={(e) => {
                  e.stopPropagation();
                  onStartQuiz(groupContent, [groupContent, ...groupedContent], 'Easy');
                }}>
                  Easy Quiz
                </DropdownMenuItem>
                <DropdownMenuItem onClick={(e) => {
                  e.stopPropagation();
                  onStartQuiz(groupContent, [groupContent, ...groupedContent], 'Hard');
                }}>
                  Hard Quiz
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
            <Button
              size="sm"
              variant="outline"
              className="h-6 px-2 text-xs bg-gray-700/50 border-gray-600 text-gray-300 hover:bg-gray-600/50"
              onClick={(e) => {
                e.stopPropagation();
                // Add match functionality here
              }}
            >
              <Shuffle className="h-3 w-3 mr-1" />
              Match
            </Button>
          </div>

          {/* Expand/Collapse button */}
          <div className="flex items-center">
            <Button
              variant="ghost"
              size="sm"
              onClick={toggleExpanded}
              className="p-1 h-8 w-8 text-white/70 hover:text-white hover:bg-white/20"
            >
              {isExpanded ? (
                <ChevronDown className="h-5 w-5" />
              ) : (
                <ChevronRight className="h-5 w-5" />
              )}
            </Button>
          </div>
        </div>

        {/* Thumbnail Gallery - hidden when expanded */}
        {!isExpanded && groupedContent.length > 0 && (
          <div className="mb-3">
            <div className="flex flex-wrap gap-2 justify-center">
              {groupedContent.slice(0, 8).map((content) => (
                <ThumbnailImageComponent 
                  key={content.id}
                  content={content}
                  onContentClick={onContentClick}
                  contextList={[groupContent, ...groupedContent]}
                />
              ))}
              {groupedContent.length > 8 && (
                <div className="w-12 h-12 rounded-md bg-white/10 flex items-center justify-center text-white/60 text-xs">
                  +{groupedContent.length - 8}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Short Description - hidden when expanded */}
        {!isExpanded && groupContent.short_description && (
          <div className="text-center">
            <p className="text-white/70 text-sm">
              {groupContent.short_description}
            </p>
          </div>
        )}

        {/* Grouped Content - Collapsible */}
        {isExpanded && groupedContent.length > 0 && (
          <div className="mt-4 space-y-2 pl-4 border-l-2 border-purple-400/30">
            {groupedContent.map((content) => (
              <div key={content.id} className={cn(
                "transition-all duration-200 rounded-lg",
                activeContentId === content.id && "ring-4 ring-yellow-400/80 bg-yellow-500/20 shadow-lg shadow-yellow-400/20"
              )}>
                <NestedContentCard
                  content={content}
                  onClick={(e) => handleNestedContentClick(content, e)}
                  onStartQuiz={onStartQuiz}
                  contextList={[groupContent, ...groupedContent]}
                />
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
};

interface NestedContentCardProps {
  content: Content;
  onClick: (e: React.MouseEvent) => void;
  onStartQuiz: (content: Content, contextList: Content[], level: 'Easy' | 'Hard') => void;
  contextList: Content[];
}

const NestedContentCard: React.FC<NestedContentCardProps> = ({
  content,
  onClick,
  onStartQuiz,
  contextList
}) => {
  const { data: imageUrl } = useContentImage(content.imageid);

  const getContentIcon = () => {
    if (content.videoid || content.videoid2) return <Play className="h-3 w-3" />;
    if (content.url) return <BookOpen className="h-3 w-3" />;
    return <BookOpen className="h-3 w-3" />;
  };

  const getContentTypeColor = () => {
    if (content.videoid || content.videoid2) return 'bg-red-500/20 text-red-200';
    if (content.url) return 'bg-blue-500/20 text-blue-200';
    return 'bg-green-500/20 text-green-200';
  };

  return (
    <Card 
      className="bg-white/5 border-white/10 hover:bg-white/10 cursor-pointer transition-all duration-200 hover:scale-[1.01]"
      onClick={onClick}
    >
      <CardContent className="p-3">
        <div className="flex items-center gap-3">
          {imageUrl && (
            <div className="w-12 h-15 rounded-md overflow-hidden flex-shrink-0">
              <img 
                src={imageUrl} 
                alt={content.title || 'Content'} 
                className="w-full h-full object-cover"
              />
            </div>
          )}

          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 mb-1">
              <Badge className={cn("text-xs", getContentTypeColor())}>
                {getContentIcon()}
              </Badge>
            </div>

            <h4 className="text-white font-medium text-sm mb-1 line-clamp-2">
              {content.title || content.short_description || 'Untitled Content'}
            </h4>

            <div className="flex items-center gap-2 mt-2">
              <CompactContentDifficultyIndicator contentId={content.id} />
              <div className="scale-75">
                <ContentRatingButtons 
                  contentId={content.id} 
                  compact={true}
                />
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default GroupedContentCard;

