import { useQuery } from "@tanstack/react-query";
import type { Content } from "@shared/schema";

export type { Content };

export const useContent = (topicId?: string) => {
  return useQuery({
    queryKey: ['content', topicId],
    queryFn: async () => {
      console.log('Fetching content from API...', topicId ? `for topic ${topicId}` : 'all content');

      const url = topicId ? `/api/content?topicId=${topicId}` : '/api/content';
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error('Failed to fetch content');
      }

      const data = await response.json();
      console.log('Content fetched:', data);
      return data as Content[];
    },
    enabled: true,
  });
};

export const useContentById = (contentId: string) => {
  return useQuery({
    queryKey: ['content', contentId],
    queryFn: async () => {
      console.log('Fetching content by ID from API...', contentId);

      const response = await fetch(`/api/content/${contentId}`);

      if (!response.ok) {
        if (response.status === 404) {
          return null;
        }
        throw new Error('Failed to fetch content by ID');
      }

      const data = await response.json();
      console.log('Content by ID fetched:', data);
      return data as Content | null;
    },
    enabled: !!contentId,
  });
};

client/src/hooks/useContentImage.ts.content:
import { useQuery } from "@tanstack/react-query";

export const useContentImage = (imageid: string | null | undefined, fallbackImageLink?: string | null) => {
    return useQuery({
        queryKey: ['image', imageid, fallbackImageLink],
        queryFn: async () => {
            console.log('useContentImage called with imageid:', imageid, 'fallbackImageLink:', fallbackImageLink);

            if (!imageid && !fallbackImageLink) {
                console.log('No imageid or fallback provided, returning null');
                return null;
            }

            if (imageid) {
                // Check if imageid is already a direct URL
                if (imageid.startsWith('http://') || imageid.startsWith('https://')) {
                    console.log('imageid is a direct URL, using it directly:', imageid);
                    return imageid;
                }

                console.log('Looking for image record with id:', imageid);

                try {
                    const response = await fetch(`/api/images/${imageid}`);

                    if (!response.ok) {
                        if (response.status === 404) {
                            console.log('Image not found, falling back to content imagelink:', fallbackImageLink);
                            return fallbackImageLink || null;
                        }
                        throw new Error('Failed to fetch image');
                    }

                    const imageRecord = await response.json();
                    console.log('Image record found:', imageRecord);

                    if (imageRecord?.imagelink) {
                        console.log('Using imagelink from image table:', imageRecord.imagelink);
                        return imageRecord.imagelink;
                    }

                    console.log('No imagelink in image record, falling back to content imagelink:', fallbackImageLink);
                    return fallbackImageLink || null;
                } catch (error) {
                    console.error('Error fetching image record:', error);
                    console.log('Falling back to content imagelink:', fallbackImageLink);
                    return fallbackImageLink || null;
                }
            }

            // If no imageid but we have fallback, use it
            console.log('No imageid, using fallback imagelink:', fallbackImageLink);
            return fallbackImageLink || null;
        },
        enabled: !!(imageid || fallbackImageLink)
    });
};

client/src/hooks/useContentMedia.ts.content:
import { useQuery } from "@tanstack/react-query";
import { Content } from "@/hooks/useContent";

const getYouTubeEmbedUrl = (url: string | null | undefined): string | null => {
    if (!url) return null;

    // Handle YouTube Shorts URLs
    const shortsRegex = /(?:https?:\/\/)?(?:www\.)?youtube\.com\/shorts\/([a-zA-Z0-9_-]{11})/;
    const shortsMatch = url.match(shortsRegex);
    if (shortsMatch && shortsMatch[1]) {
        return `https://www.youtube.com/embed/${shortsMatch[1]}`;
    }

    // Handle regular YouTube URLs
    const youtubeRegex = /(?:https?:\/\/)?(?:www\.)?(?:youtube\.com\/(?:[^\/\n\s]+\/\S+\/|(?:v|e(?:mbed)?)\/|\S*?[?&]v=)|youtu\.be\/)([a-zA-Z0-9_-]{11})/;
    const match = url.match(youtubeRegex);
    return match && match[1] ? `https://www.youtube.com/embed/${match[1]}` : null;
};

export const useContentMedia = (content: Content | null) => {
    console.log('useContentMedia hook triggered. Content:', content);

    // Use video links directly from content table columns
    const videoEmbedUrl = getYouTubeEmbedUrl(content?.videoid);
    const video2EmbedUrl = getYouTubeEmbedUrl(content?.videoid2);

    // Create mock video data objects for compatibility with existing components
    const videoData = content?.videoid ? { 
        id: content.videoid, 
        videolink: content.videoid,
        video_name: null
    } : null;

    const video2Data = content?.videoid2 ? { 
        id: content.videoid2, 
        videolink: content.videoid2,
        video_name: null
    } : null;

    return { videoData, video2Data, videoEmbedUrl, video2EmbedUrl };
};

client/src/hooks/useMatchingAttempts.ts.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

export interface MatchingAttempt {
  id: string;
  student_id: string;
  matching_id: string;
  answers: any; // JSON data containing the matching pairs
  score: number | null;
  max_score: number | null;
  is_correct: boolean | null;
  time_start: Date;
  time_end: Date | null;
  duration_seconds: number | null;
  attempt_number: number;
  created_at: Date;
}

export interface CreateMatchingAttempt {
  id: string;
  student_id: string;
  matching_id: string;
  answers?: any;
  score?: number;
  max_score?: number;
  is_correct?: boolean;
  time_start?: Date;
  time_end?: Date;
  duration_seconds?: number;
  attempt_number?: number;
}

export const useMatchingAttempts = (studentId: string, matchingId?: string) => {
  return useQuery({
    queryKey: ['/api/matching-attempts/student', studentId, matchingId],
    queryFn: () => apiRequest(`/api/matching-attempts/student/${studentId}${matchingId ? `?matchingId=${matchingId}` : ''}`),
    enabled: !!studentId,
  });
};

export const useCreateMatchingAttempt = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (attempt: CreateMatchingAttempt) => 
      apiRequest('/api/matching-attempts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(attempt),
      }),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/matching-attempts/student', variables.student_id] });
    },
  });
};

export const useUpdateMatchingAttempt = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: ({ id, updates }: { id: string; updates: Partial<MatchingAttempt> }) =>
      apiRequest(`/api/matching-attempts/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updates),
      }),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/matching-attempts'] });
    },
  });
};

export const useMatchingAttempt = (id: string) => {
  return useQuery({
    queryKey: ['/api/matching-attempts', id],
    queryFn: () => apiRequest(`/api/matching-attempts/${id}`),
    enabled: !!id,
  });
};

client/src/hooks/useQuiz.ts.content:
import { useState, useCallback, useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import type { Content } from "./useContent";

interface UseQuizProps {
  content: Content | null;
  onClose: () => void;
  startQuizDirectly?: boolean;
  level?: 'easy' | 'hard' | null;
}

export const useQuiz = ({ content, onClose, startQuizDirectly = false, level }: UseQuizProps) => {
  const [quizMode, setQuizMode] = useState(false);
  const [assignmentTry, setAssignmentTry] = useState<any>(null);
  const [studentTry, setStudentTry] = useState<any>(null);
  const [questionIds, setQuestionIds] = useState<string[]>([]);
  const { toast } = useToast();

  const startQuiz = useCallback(async (level?: 'easy' | 'hard') => {
    if (!content) return;

    // Fetch questions for this content
    const url = level 
      ? `/api/questions?contentId=${content.id}&level=${level}`
      : `/api/questions?contentId=${content.id}`;

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Failed to fetch questions');
      }
      const questions = await response.json();

      if (!questions || questions.length === 0) {
          console.log("No questions available for this content.", level ? `Level: ${level}` : '');
          toast({
              title: "No Quiz Available",
              description: `There are no ${level ? level.toLowerCase() + ' ' : ''}questions for this content yet. Check back later!`,
          });
          if (startQuizDirectly) onClose();
          return;
      }

      const randomizedQuestionIds = questions.map((q: any) => q.id).sort(() => Math.random() - 0.5);

      const hocsinh_id = 'user-123-placeholder';

      // Create quiz session using assignment_student_try (eliminates need for assignment table)
      const quizSessionData = {
        hocsinh_id: hocsinh_id,
        contentID: content.id,
        questionIDs: JSON.stringify(randomizedQuestionIds),
        start_time: new Date().toISOString(),
        typeoftaking: level || 'Overview'
      };

      const sessionResponse = await fetch('/api/assignment-student-tries', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(quizSessionData)
      });

      if (!sessionResponse.ok) {
        throw new Error('Failed to create quiz session');
      }

      const quizSession = await sessionResponse.json();

      console.log('Quiz session created:', quizSession);

      setAssignmentTry(quizSession);
      setStudentTry(null); // Individual records will be created per question
      setQuestionIds(randomizedQuestionIds);
      setQuizMode(true);
    } catch (error) {
        console.error("Error starting quiz:", error);
        toast({
            title: "Error Starting Quiz",
            description: "Could not start the quiz due to a server error. Please try again.",
            variant: "destructive",
        });
        if (startQuizDirectly) onClose();
        return;
    }
  }, [content, onClose, startQuizDirectly, toast]);

  const closeQuiz = useCallback(() => {
    setQuizMode(false);
    setAssignmentTry(null);
    setStudentTry(null);
    setQuestionIds([]);
    onClose();
  }, [onClose]);

  // Auto-start quiz when startQuizDirectly is true and level is provided
  useEffect(() => {
    if (startQuizDirectly && content && level) {
      startQuiz(level);
    }
  }, [startQuizDirectly, content, level, startQuiz]);

  return {
    quizMode,
    assignmentTry,
    studentTry,
    questionIds,
    startQuiz,
    closeQuiz,
    setStudentTry
  };
};

client/src/hooks/useTopicMatching.ts.content:
import { useQuery } from '@tanstack/react-query';

interface MatchingActivity {
  id: string;
  topicid: string | null;
  type: string | null;
  description: string | null;
  subject: string | null;
  topic: string | null;
}

interface Topic {
  id: string;
  topic: string;
  parentid: string | null;
}

const fetchMatchingByTopic = async (topicId: string): Promise<MatchingActivity[]> => {
  const response = await fetch(`/api/matching/topic/${topicId}`);
  if (!response.ok) {
    throw new Error('Failed to fetch matching activities');
  }
  return response.json();
};

const fetchAllTopics = async (): Promise<Topic[]> => {
  const response = await fetch('/api/topics');
  if (!response.ok) {
    throw new Error('Failed to fetch topics');
  }
  return response.json();
};

const fetchAllMatchingActivities = async (): Promise<MatchingActivity[]> => {
  const response = await fetch('/api/matching');
  if (!response.ok) {
    throw new Error('Failed to fetch matching activities');
  }
  return response.json();
};

export const useTopicMatching = (topicId: string) => {
  const { data: matchingActivities, isLoading, error } = useQuery({
    queryKey: ['matchingByTopic', topicId],
    queryFn: () => fetchMatchingByTopic(topicId),
    enabled: !!topicId,
  });

  const hasMatchingActivities = matchingActivities && matchingActivities.length > 0;

  return {
    matchingActivities,
    hasMatchingActivities,
    isLoading,
    error
  };
};

export const useParentTopicMatching = (parentTopicId: string) => {
  const { data: allTopics } = useQuery({
    queryKey: ['topics'],
    queryFn: fetchAllTopics,
  });

  const { data: allMatchingActivities } = useQuery({
    queryKey: ['matching'],
    queryFn: fetchAllMatchingActivities,
  });

  // Get all subtopics under this parent
  const subtopics = allTopics?.filter(topic => topic.parentid === parentTopicId) || [];
  const subtopicIds = subtopics.map(topic => topic.id);

  // Include the parent topic itself
  const allRelevantTopicIds = [parentTopicId, ...subtopicIds];

  // Get all matching activities for parent and subtopics
  const parentMatchingActivities = allMatchingActivities?.filter(activity => 
    activity.topicid && allRelevantTopicIds.includes(activity.topicid)
  ) || [];

  const hasParentMatchingActivities = parentMatchingActivities.length > 0;

  return {
    parentMatchingActivities,
    hasParentMatchingActivities,
    subtopics,
    isLoading: !allTopics || !allMatchingActivities,
  };
};

