                          <td className="p-3">
                            {editingId === student.id ? (
                              <Input
                                value={editData.category || ''}
                                onChange={(e) => setEditData({...editData, category: e.target.value})}
                                className="w-full"
                              />
                            ) : (
                              <Badge variant="secondary">{student.category || 'Unknown'}</Badge>
