import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Trophy, 
  BookOpen, 
  Users, 
  Target, 
  ArrowRight, 
  Star, 
  Zap,
  Brain,
  Globe,
  Award
} from "lucide-react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import merakiLogo from "@assets/MERAKI new logo vo6-03_1750301582337.png";

const Homepage = () => {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const [currentTime, setCurrentTime] = useState(new Date());

  // Update time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  // Fetch user's recent activity
  const { data: streakData } = useQuery({
    queryKey: [`/api/streaks/${user?.id}`],
    enabled: !!user?.id,
  });

  const { data: leaderboardData } = useQuery({
    queryKey: ["/api/student-tries-leaderboard"],
  });

  const userRank = leaderboardData?.find((entry: any) => entry.student_id === user?.id)?.rank || "N/A";

  const getGreeting = () => {
    const hour = currentTime.getHours();
    if (hour < 12) return "Good Morning";
    if (hour < 17) return "Good Afternoon";
    return "Good Evening";
  };

  const formatDate = () => {
    return currentTime.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const quickActions = [
    {
      title: "Bowl & Challenge",
      description: "Interactive learning modules",
      icon: BookOpen,
      color: "bg-blue-500",
