import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { CheckCircle, AlertCircle, User, Mail } from "lucide-react";

export default function ValidateAccess() {
  const [studentId, setStudentId] = useState("");
  const [merakiEmail, setMerakiEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [googleUser, setGoogleUser] = useState<any>(null);
  const { toast } = useToast();

  useEffect(() => {
    // Get Google user info from session
    fetch('/api/auth/google-user')
      .then(res => res.json())
      .then(data => {
        if (data.user) {
          setGoogleUser(data.user);
        } else {
          window.location.href = '/';
        }
      })
      .catch(() => {
        window.location.href = '/';
      });
  }, []);

  const handleValidation = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!studentId.trim() && !merakiEmail.trim()) {
      toast({
        title: "Required Information",
        description: "Please enter either your Student ID or Meraki Email",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/validate-student", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          studentId: studentId.trim(),
          merakiEmail: merakiEmail.trim()
        }),
      });

      const result = await response.json();

      if (response.ok) {
        toast({
          title: "Access Granted",
          description: "Welcome! Redirecting to learning platform...",
        });
        setTimeout(() => {
          window.location.href = "/";
        }, 1500);
      } else {
        toast({
          title: "Validation Failed", 
          description: result.message || "Student ID or Meraki Email not found",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Connection error. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleRequestAccess = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/request-access", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        toast({
          title: "Access Request Sent",
          description: "Admin has been notified. You'll receive an email when access is approved.",
        });
      } else {
        toast({
          title: "Request Failed",
          description: "Unable to send access request. Please contact admin directly.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Connection error. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (!googleUser) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <CheckCircle className="h-12 w-12 text-green-600" />
          </div>
          <CardTitle className="text-2xl font-bold">Validate Your Access</CardTitle>
          <CardDescription>
            Hi {googleUser.firstName}! Please provide your Student ID or Meraki Email to access the learning platform.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="bg-gray-50 dark:bg-gray-800 p-3 rounded-lg">
            <div className="flex items-center gap-2 text-sm">
              <Mail className="h-4 w-4 text-blue-600" />
              <span className="font-medium">Google Account:</span>
              <span>{googleUser.email}</span>
            </div>
          </div>

          <form onSubmit={handleValidation} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="studentId">Student ID (if available)</Label>
              <Input
                id="studentId"
                type="text"
                placeholder="Enter your Student ID"
                value={studentId}
                onChange={(e) => setStudentId(e.target.value)}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="merakiEmail">Meraki Email (if available)</Label>
              <Input
                id="merakiEmail"
                type="email"
                placeholder="Enter your Meraki Email"
                value={merakiEmail}
                onChange={(e) => setMerakiEmail(e.target.value)}
              />
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading || (!studentId.trim() && !merakiEmail.trim())}
            >
              <User className="h-4 w-4 mr-2" />
              {isLoading ? "Validating..." : "Validate Access"}
            </Button>
          </form>

          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <span className="w-full border-t" />
            </div>
            <div className="relative flex justify-center text-xs uppercase">
              <span className="bg-background px-2 text-muted-foreground">Or</span>
            </div>
          </div>

          <div className="text-center space-y-3">
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Don't have your Student ID or Meraki Email?
            </p>
            <Button 
              variant="outline"
              onClick={handleRequestAccess}
              disabled={isLoading}
              className="w-full"
            >
              <AlertCircle className="h-4 w-4 mr-2" />
              Request Access from Admin
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Writing.tsx.content:
import { useState, useEffect } from "react";
import { Header } from "@/components/shared";
import { WritingJournal } from "@/components/WritingJournal";
import { WritingTopicSelection } from "@/components/WritingTopicSelection";
import { StructuredEssayWriter } from "@/components/StructuredEssayWriter";

const WritingPage = () => {
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [writingFlow, setWritingFlow] = useState<
        "journal" | "topics" | "essay"
    >("journal");
    const [selectedCategory, setSelectedCategory] = useState<string>("");
    const [selectedTopic, setSelectedTopic] = useState<{
        id: string;
        title: string;
        description: string;
    }>({
        id: "",
        title: "",
        description: "",
    });
    const [writingPrompts, setWritingPrompts] = useState<any[]>([]);

    useEffect(() => {
        const userData = localStorage.getItem("currentUser");
        if (userData) {
            setCurrentUser(JSON.parse(userData));
        } else {
            // Provide a demo user when no user is found in localStorage
            setCurrentUser({
                id: "demo-user",
                full_name: "Demo User",
                first_name: "Demo",
            });
        }
    }, []);

    // Fetch writing prompts from API
    useEffect(() => {
        const fetchWritingPrompts = async () => {
            try {
                const response = await fetch('/api/writing-prompts');
                if (response.ok) {
                    const prompts = await response.json();
                    setWritingPrompts(prompts);
                }
            } catch (error) {
                console.error('Failed to fetch writing prompts:', error);
            }
        };

        fetchWritingPrompts();
    }, []);

    const handleCategorySelect = (category: string) => {
        setSelectedCategory(category);
        setWritingFlow("topics");
    };

    const handleTopicSelect = (topicId: string) => {
        // Find the topic from fetched writing prompts
        const foundPrompt = writingPrompts.find(prompt => prompt.id === topicId);

        const topic = foundPrompt || {
            title: "Creative Writing",
            description: "Write about your chosen topic."
        };

        setSelectedTopic({
            id: topicId,
            title: topic.title,
            description: topic.description,
        });
        setWritingFlow("essay");
    };

    const handleBackToJournal = () => {
        setWritingFlow("journal");
        setSelectedCategory("");
    };

    const handleBackToTopics = () => {
        setWritingFlow("topics");
    };

    if (!currentUser) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700 text-white">
                <Header />
                <div className="container mx-auto p-4 md:p-8">
                    <div className="text-center">
                        <h1 className="text-3xl font-bold text-white mb-3">
                            Writing
                        </h1>
                        <p className="text-lg text-white/80">
                            Please log in to access the writing system.
                        </p>
                    </div>
                </div>
            </div>
        );
    }

    if (writingFlow === "topics") {
        return (
            <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700">
                <Header />
                <WritingTopicSelection
                    category={selectedCategory}
                    onBack={handleBackToJournal}
                    onTopicSelect={handleTopicSelect}
                />
            </div>
        );
    }

    if (writingFlow === "essay") {
        return (
            <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700">
                <Header />
                <StructuredEssayWriter
                    topicTitle={selectedTopic.title}
                    topicDescription={selectedTopic.description}
                    studentId={currentUser.id}
                    onBack={handleBackToTopics}
                />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700">
            <Header />
            <WritingJournal
                studentId={currentUser.id}
                studentName={currentUser.full_name || currentUser.first_name}
                onCategorySelect={handleCategorySelect}
            />
        </div>
    );
};

export default WritingPage;

