import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Loader2, Star, FileText, User, Calendar, BookOpen } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface WritingSubmission {
  id: string;
  student_id: string;
  prompt_id: string;
  title: string;
  opening_paragraph: string;
  body_paragraph_1: string;
  body_paragraph_2: string;
  body_paragraph_3: string;
  conclusion_paragraph: string;
  full_essay: string;
  ai_feedback: any;
  overall_score: number;
  word_count: number;
  status: string;
  created_at: string;
  updated_at: string;
}

interface WritingSubmissionPopupProps {
  submission: WritingSubmission | null;
  isOpen: boolean;
  onClose: () => void;
  studentName?: string;
  onGradingComplete?: () => void;
}

export const WritingSubmissionPopup: React.FC<WritingSubmissionPopupProps> = ({
  submission,
  isOpen,
  onClose,
  studentName,
  onGradingComplete
}) => {
  const [isGrading, setIsGrading] = useState(false);
  const [aiScore, setAiScore] = useState<number>(submission?.overall_score || 0);
  const [aiFeedback, setAiFeedback] = useState<string>('');
  const { toast } = useToast();

  if (!submission) return null;

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  const getWordCount = (text: string) => {
    return text.trim() ? text.trim().split(/\s+/).length : 0;
  };

  const calculateActualWordCount = () => {
    const totalWords = [
      submission.opening_paragraph,
      submission.body_paragraph_1,
      submission.body_paragraph_2,
      submission.body_paragraph_3,
      submission.conclusion_paragraph
    ].reduce((total, paragraph) => total + getWordCount(paragraph || ''), 0);
    return totalWords;
  };

  const { handleAIGrading, isGrading, aiScore, setAiScore, aiFeedback, setAiFeedback } = useAIGrading({
    submission,
    calculateActualWordCount,
    toast,
    onGradingComplete
  });

  const getScoreColor = (score: number) => {
    if (score >= 90) return "text-green-600";
    if (score >= 80) return "text-blue-600";
    if (score >= 70) return "text-yellow-600";
    return "text-red-600";
  };

  const getScoreBadgeVariant = (score: number) => {
    if (score >= 90) return "default";
    if (score >= 80) return "secondary";
    if (score >= 70) return "outline";
    return "destructive";
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Writing Submission
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Header Information */}
          <Card>
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div className="space-y-1">
                  <h3 className="text-lg font-semibold">{submission.title || 'Untitled Essay'}</h3>
                  <div className="flex items-center gap-4 text-sm text-gray-600">
                    <div className="flex items-center gap-1">
                      <User className="h-4 w-4" />
                      {studentName || submission.student_id}
                    </div>
                    <div className="flex items-center gap-1">
                      <Calendar className="h-4 w-4" />
                      {formatDate(submission.created_at)}
                    </div>
                    <div className="flex items-center gap-1">
                      <BookOpen className="h-4 w-4" />
                      {calculateActualWordCount()} words
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <Badge variant="outline">{submission.status}</Badge>
                  {submission.overall_score > 0 && (
                    <Badge variant={getScoreBadgeVariant(submission.overall_score)}>
                      {submission.overall_score}/100
                    </Badge>
                  )}
                </div>
              </div>
            </CardHeader>
          </Card>

          {/* Essay Content */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Essay Content</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Introduction */}
              {submission.opening_paragraph && (
                <div>
                  <h4 className="font-semibold text-sm mb-2 text-gray-700">Introduction</h4>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <p className="whitespace-pre-wrap leading-relaxed">{submission.opening_paragraph}</p>
                  </div>
                </div>
              )}

              {/* Body Paragraphs */}
              {[
                { content: submission.body_paragraph_1, title: "Body Paragraph 1" },
                { content: submission.body_paragraph_2, title: "Body Paragraph 2" },
                { content: submission.body_paragraph_3, title: "Body Paragraph 3" }
              ].map((paragraph, index) => 
                paragraph.content && (
                  <div key={index}>
                    <h4 className="font-semibold text-sm mb-2 text-gray-700">{paragraph.title}</h4>
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <p className="whitespace-pre-wrap leading-relaxed">{paragraph.content}</p>
                    </div>
                  </div>
                )
              )}

              {/* Conclusion */}
              {submission.conclusion_paragraph && (
                <div>
                  <h4 className="font-semibold text-sm mb-2 text-gray-700">Conclusion</h4>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <p className="whitespace-pre-wrap leading-relaxed">{submission.conclusion_paragraph}</p>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>

          {/* AI Grading Section */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center gap-2">
                <Star className="h-5 w-5" />
                AI Grading
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {!submission.ai_feedback && !aiFeedback ? (
                <div className="text-center py-6">
                  <p className="text-gray-600 mb-4">This essay has not been graded by AI yet.</p>
                  <Button 
                    onClick={handleAIGrading} 
                    disabled={isGrading}
                    className="flex items-center gap-2"
                  >
                    {isGrading ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <Star className="h-4 w-4" />
                    )}
                    {isGrading ? 'Grading Essay...' : 'Grade with AI'}
                  </Button>
                </div>
              ) : (
                <div className="space-y-4">
                  {/* Score Display */}
                  <div className="flex items-center gap-4">
                    <Label htmlFor="score">Overall Score:</Label>
                    <div className="flex items-center gap-2">
                      <Input
                        id="score"
                        type="number"
                        min="0"
                        max="100"
                        value={aiScore}
                        onChange={(e) => setAiScore(parseInt(e.target.value) || 0)}
                        className="w-20"
                      />
                      <span className="text-sm text-gray-600">/ 100</span>
                      <span className={`font-semibold ${getScoreColor(aiScore)}`}>
                        {aiScore >= 90 ? 'Excellent' : 
                         aiScore >= 80 ? 'Good' : 
                         aiScore >= 70 ? 'Satisfactory' : 'Needs Improvement'}
                      </span>
                    </div>
                  </div>

                  {/* Feedback */}
                  <div>
                    <Label htmlFor="feedback">AI Feedback:</Label>
                    <Textarea
                      id="feedback"
                      value={aiFeedback || (submission.ai_feedback?.content || '')}
                      onChange={(e) => setAiFeedback(e.target.value)}
                      rows={8}
                      className="mt-2"
                      placeholder="AI feedback will appear here..."
                    />
                  </div>

                  {/* Re-grade Button */}
                  <Button 
                    onClick={handleAIGrading} 
                    disabled={isGrading}
                    variant="outline"
                    className="flex items-center gap-2"
                  >
                    {isGrading ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <Star className="h-4 w-4" />
                    )}
                    {isGrading ? 'Re-grading...' : 'Re-grade with AI'}
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
};

client/src/components/CenteredObjectUplo.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { Dashboard } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";

interface CenteredObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A centered file upload component that renders as a button and provides a popup dialog interface for
 * file management without dimming the entire page.
 * 
 * Features:
 * - Renders as a customizable button that opens a centered upload dialog
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * - Dialog is centered on screen and doesn't dim background
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 */
export function CenteredObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 52428800, // 50MB default for debate files
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: CenteredObjectUploaderProps) {
  const [showDialog, setShowDialog] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
        setShowDialog(false); // Close dialog after successful upload
      })
  );

  return (
    <>
      <Button onClick={() => setShowDialog(true)} className={buttonClassName}>
        {children}
      </Button>

      <Dialog open={showDialog} onOpenChange={setShowDialog}>
        <DialogContent className="max-w-2xl w-full max-h-[80vh] p-0 overflow-hidden">
          <DialogHeader className="p-6 pb-0">
            <DialogTitle>Upload File</DialogTitle>
          </DialogHeader>
          <div className="p-6 pt-4">
            <Dashboard
              uppy={uppy}
              plugins={[]}
              proudlyDisplayPoweredByUppy={false}
              theme="auto"
              width="100%"
              height={400}
              showProgressDetails={true}
              note={`Files up to ${Math.round(maxFileSize / 1024 / 1024)}MB are allowed`}
            />
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

