    if (!difficulty) return null;

    const starCount = difficulty === 'easy' ? 1 : difficulty === 'medium' ? 2 : 3;
    return (
      <div className="flex gap-1">
        {[...Array(3)].map((_, i) => (
          <Star
            key={i}
