      <PopoverTrigger asChild>
        <Button 
          variant="outline" 
          size={compact ? "sm" : "default"}
          className={cn(
            "text-blue-600 hover:bg-blue-500/20 hover:text-blue-600 bg-blue-500/10 border-blue-400/50",
