            <h4 className="font-medium text-sm">My Personal Note</h4>
          </div>
          <div className="bg-gray-50 rounded-md p-3 text-sm text-gray-700 max-h-32 overflow-y-auto">
            {existingRating?.personal_note || 'No note available'}
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
};

