                Hard
              </Button>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

client/src/components/topics/GroupCard.t.content:
import React from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { ChevronDown, ChevronRight, Users, BookOpen } from 'lucide-react';
import { Content } from '@/hooks/useContent';
import { ContentCard } from './ContentCard';

interface Topic {
  id: string;
  topic: string;
  short_summary?: string;
  challengesubject?: string;
  image?: string;
  parentid?: string;
  showstudent?: boolean;
}

interface Image {
  id: string;
  imagelink: string | null;
  contentid: string | null;
  default: string | null;
}

interface GroupCardProps {
  topic: Topic;
  subtopics: Topic[];
  topicContent: Content[];
  allImages: Image[] | undefined;
  isExpanded: boolean;
  isActive: boolean;
  openContent: string[];
  activeContentId: string | null;
  onToggleTopic: (topicId: string) => void;
  onToggleContent: (contentKey: string) => void;
  onContentClick: (info: { content: Content; contextList: Content[] }) => void;
  onSubtopicClick: (topicId: string) => void;
  onStartQuiz: (content: Content, contextList: Content[], level?: 'Easy' | 'Hard') => void;
  onStartTopicQuiz: (topicId: string, level: 'Overview' | 'Easy' | 'Hard', topicName: string) => void;
  onStartTopicMatching: (topicId: string, topicName: string) => void;
  onStartGroupMatching: (matchingId: string, matchingTitle: string) => void;
  getTopicContent: (topicId: string) => Content[];
}

export const GroupCard: React.FC<GroupCardProps> = ({
  topic,
  subtopics,
  topicContent,
  allImages,
  isExpanded,
  isActive,
  openContent,
  activeContentId,
  onToggleTopic,
  onToggleContent,
  onContentClick,
  onSubtopicClick,
  onStartQuiz,
  onStartTopicQuiz,
  onStartTopicMatching,
  onStartGroupMatching,
  getTopicContent
}) => {
  const contentKey = `topic-${topic.id}`;
  const isContentExpanded = openContent.includes(contentKey);

  return (
    <Card className={`transition-all duration-200 ${isActive ? 'ring-2 ring-blue-500' : ''} ${isExpanded ? 'bg-blue-50/30' : ''}`}>
      <CardContent className="p-4">
        {/* Topic Header */}
        <div className="flex items-center justify-between mb-3">
          <Button
            variant="ghost"
            onClick={() => onToggleTopic(topic.id)}
            className="flex-1 justify-start p-0 h-auto text-left"
          >
            <div className="flex items-center gap-2">
              {isExpanded ? (
                <ChevronDown className="h-4 w-4 text-blue-600" />
              ) : (
                <ChevronRight className="h-4 w-4 text-gray-400" />
              )}
              <Users className="h-4 w-4 text-blue-600" />
              <div>
                <h3 className="font-semibold text-gray-800 text-sm">{topic.topic}</h3>
                {topic.short_summary && (
                  <p className="text-xs text-gray-600 mt-1 line-clamp-2">{topic.short_summary}</p>
                )}
              </div>
            </div>
          </Button>

          {topic.challengesubject && (
            <Badge variant="outline" className="text-xs ml-2">
              {topic.challengesubject}
            </Badge>
          )}
        </div>

        {/* Expanded Content */}
        {isExpanded && (
          <div className="ml-6 space-y-3">
            {/* Subtopics */}
            {subtopics.length > 0 && (
              <div>
                <h4 className="text-xs font-medium text-gray-600 mb-2 uppercase tracking-wide">Subtopics</h4>
                {subtopics.map(subtopic => (
                  <Button
                    key={subtopic.id}
                    variant="ghost"
                    size="sm"
                    onClick={() => onSubtopicClick(subtopic.id)}
                    className="block w-full text-left justify-start mb-1 h-auto p-2"
                  >
                    <span className="text-xs text-blue-600">{subtopic.topic}</span>
                  </Button>
                ))}
              </div>
            )}

            {/* Topic Quiz Buttons */}
            <div className="flex gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onStartTopicQuiz(topic.id, 'Overview', topic.topic)}
                className="text-blue-600 hover:bg-blue-50 text-xs"
              >
                Overview Quiz
              </Button>
              <Button
                variant="outline"  
                size="sm"
                onClick={() => onStartTopicMatching(topic.id, topic.topic)}
                className="text-purple-600 hover:bg-purple-50 text-xs"
              >
                Matching
              </Button>
            </div>

            {/* Topic Content */}
            {topicContent.length > 0 && (
              <div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onToggleContent(contentKey)}
                  className="flex items-center gap-2 mb-2 text-xs"
                >
                  {isContentExpanded ? (
                    <ChevronDown className="h-3 w-3" />
                  ) : (
                    <ChevronRight className="h-3 w-3" />
                  )}
                  <BookOpen className="h-3 w-3" />
                  Content ({topicContent.length})
                </Button>

                {isContentExpanded && (
                  <div className="ml-5 space-y-2">
                    {topicContent.map(content => (
                      <ContentCard
                        key={content.id}
                        content={content}
                        contextList={topicContent}
                        allImages={allImages}
                        isActive={activeContentId === content.id}
                        onContentClick={onContentClick}
                        onStartQuiz={onStartQuiz}
                      />
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
};

client/src/components/topics/TopicCard.t.content:
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";
import { BookOpen, Target, HelpCircle } from "lucide-react";
import { Topic } from "@/hooks/useTopics";
import { useState, useEffect } from "react";
import TopicQuizRunner from "./TopicQuizRunner";

interface TopicCardProps {
  topic: Topic;
}

const TopicCard = ({ topic }: TopicCardProps) => {
  const [topicQuizInfo, setTopicQuizInfo] = useState<{
    topicId: string;
    level: "Overview" | "Easy" | "Hard";
    topicName: string;
  } | null>(null);

  const [availableQuizLevels, setAvailableQuizLevels] = useState<{
    Overview: boolean;
    Easy: boolean;
    Hard: boolean;
  }>({ Overview: false, Easy: false, Hard: false });

  useEffect(() => {
    const checkAvailableQuizLevels = async () => {
      const levels = ["Overview", "Easy", "Hard"] as const;
      const availability = { Overview: false, Easy: false, Hard: false };

      for (const level of levels) {
        try {
          const response = await fetch(`/api/questions?topicId=${topic.id}&level=${level}`);
          if (response.ok) {
            const questions = await response.json();
            availability[level] = questions && questions.length > 0;
          }
        } catch (error) {
          console.error(`Error checking ${level} questions for topic ${topic.id}:`, error);
        }
      }

      setAvailableQuizLevels(availability);
    };

    checkAvailableQuizLevels();
  }, [topic.id]);

  const handleStartTopicQuiz = (level: "Overview" | "Easy" | "Hard") => {
    if (!availableQuizLevels[level]) {
      return; // Don't start quiz if no questions available
    }

    setTopicQuizInfo({
      topicId: topic.id,
      level: level,
      topicName: topic.topic
    });
  };

  const closeTopicQuiz = () => {
    setTopicQuizInfo(null);
  };

  return (
    <>
      <Card className="p-4 hover:shadow-md transition-shadow">
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center gap-2">
            <div className="p-1 rounded bg-blue-100">
              <Target className="h-4 w-4 text-blue-600" />
            </div>
          </div>
          {topic.challengesubject && (
            <Badge variant="outline" className="text-xs">
              {topic.challengesubject}
            </Badge>
          )}
        </div>

        <Link to={`/content/${topic.id}`}>
          <div className="cursor-pointer">
            <h3 className="font-medium text-gray-800 text-sm mb-1">{topic.topic}</h3>
            {topic.short_summary && (
              <p className="text-xs text-gray-600 line-clamp-2 mb-3">{topic.short_summary}</p>
            )}
          </div>
        </Link>

        {/* Quiz Buttons - Only show if any quiz level has questions */}
        {(availableQuizLevels.Overview || availableQuizLevels.Easy || availableQuizLevels.Hard) && (
          <div className="flex gap-1 mt-3 pt-2 border-t border-gray-100">
            {availableQuizLevels.Overview && (
              <Button
                variant="outline"
                size="sm"
                className="text-blue-600 hover:bg-blue-50 border-blue-200 text-xs px-2 py-1 h-6 flex-1"
                onClick={(e) => {
                  e.stopPropagation();
                  handleStartTopicQuiz("Overview");
                }}
                title="Overview Quiz"
              >
                <HelpCircle className="h-3 w-3 mr-1" />
                Overview
              </Button>
            )}

            {availableQuizLevels.Easy && (
              <Button
                variant="outline"
                size="sm"
                className="text-green-600 hover:bg-green-50 border-green-200 text-xs px-2 py-1 h-6 flex-1"
                onClick={(e) => {
                  e.stopPropagation();
                  handleStartTopicQuiz("Easy");
                }}
                title="Easy Quiz"
              >
                <HelpCircle className="h-3 w-3 mr-1" />
                Easy
              </Button>
            )}

            {availableQuizLevels.Hard && (
              <Button
                variant="outline"
                size="sm"
                className="text-red-600 hover:bg-red-50 border-red-200 text-xs px-2 py-1 h-6 flex-1"
                onClick={(e) => {
                  e.stopPropagation();
                  handleStartTopicQuiz("Hard");
                }}
                title="Hard Quiz"
              >
                <HelpCircle className="h-3 w-3 mr-1" />
                Hard
              </Button>
            )}
          </div>
        )}
      </Card>

      {/* Topic Quiz Runner */}
      {topicQuizInfo && (
        <TopicQuizRunner
          topicId={topicQuizInfo.topicId}
          level={topicQuizInfo.level}
          topicName={topicQuizInfo.topicName}
          onClose={closeTopicQuiz}
        />
      )}
    </>
  );
};

export default TopicCard;

