import type { Express } from "express";
import { getSessionMiddleware } from "../sessionAuth";
import { setupGoogleAuth } from "../googleAuth";

export function authRoutes(app: Express) {
  // Session middleware setup
  app.use(getSessionMiddleware());

  // Setup Google OAuth routes (if configured)
  setupGoogleAuth(app);

  // Current user endpoint
  app.get("/api/current-user", (req, res) => {
    if (req.session?.user) {
      res.json({ user: req.session.user });
    } else {
      res.status(401).json({ message: "Not authenticated" });
    }
  });

  // Login endpoint for students
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { identifier, password } = req.body;

      if (!identifier || !password) {
        return res.status(400).json({ message: "Student ID/Email and password are required" });
      }

      // For now, we'll use a simple hardcoded password check for GV0002
      // In production, implement proper password verification
      if (identifier === "GV0002" && password === "password") {
        // Fetch user from database
        const { UserStorage } = await import("../storage/userStorage");
        const userStorage = new UserStorage();
        const user = await userStorage.getUser("GV0002");

        if (user) {
          // Set session
          (req.session as any).userId = user.id;
          (req.session as any).user = user;

          res.json({ 
            message: "Login successful",
            user: {
              id: user.id,
              name: user.full_name,
              email: user.email,
              category: user.category
            }
          });
        } else {
          res.status(404).json({ message: "User not found" });
        }
      } else {
        res.status(401).json({ message: "Invalid credentials" });
      }
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get current user endpoint
  app.get("/api/auth/user", (req, res) => {
    if ((req.session as any)?.user) {
      res.json({ user: (req.session as any).user });
    } else {
      res.status(401).json({ message: "Not authenticated" });
    }
  });

  // Logout endpoint
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        console.error("Logout error:", err);
        res.status(500).json({ message: "Failed to logout" });
      } else {
        res.json({ message: "Logged out successfully" });
      }
    });
  });
}

server/storage/questionStorage.ts.content:
import { questions, type Question } from "@shared/schema";
import { eq, sql, and } from "drizzle-orm";
import { db } from "../db";

export class QuestionStorage {
  async getQuestions(contentId?: string, topicId?: string, level?: string): Promise<Question[]> {
    try {
      // Use raw SQL to avoid column duplication issues in Drizzle
      let sqlQuery = 'SELECT DISTINCT * FROM question WHERE 1=1';
      const params: any[] = [];
      let paramIndex = 1;

      if (contentId) {
        sqlQuery += ` AND contentid = $${paramIndex}`;
        params.push(contentId);
        paramIndex++;
      }
      if (topicId) {
        sqlQuery += ` AND topic = $${paramIndex}`;
        params.push(topicId);
        paramIndex++;
      }
      if (level) {
        sqlQuery += ` AND questionlevel = $${paramIndex}`;
        params.push(level);
        paramIndex++;
      }

      const result = await db.execute(sql.raw(sqlQuery, ...params));
      return result.rows as Question[];
    } catch (error) {
      console.error('Error fetching questions:', error);
      throw error;
    }
  }

  async getQuestionById(id: string): Promise<Question | undefined> {
    try {
      const result = await db.select().from(questions).where(eq(questions.id, id));
      return result[0];
    } catch (error) {
      console.error('Error fetching question by ID:', error);
      throw error;
    }
  }
}

server/storage/topicStorage.ts.content:
import { topics, type Topic } from "@shared/schema";
import { eq, sql, asc } from "drizzle-orm";
import { db } from "../db";

export class TopicStorage {
  async getTopics(): Promise<Topic[]> {
    try {
      return await db.select().from(topics);
    } catch (error) {
      console.error('Error fetching topics:', error);
      throw error;
    }
  }

  async getBowlChallengeTopics(): Promise<Topic[]> {
    try {
      // Return main topics that should be shown to students (parent topics with no parentid)
      // Ordered alphabetically by topic name
      return await db.select().from(topics).where(
        sql`${topics.showstudent} = true AND ${topics.parentid} IS NULL`
      ).orderBy(asc(topics.topic));
    } catch (error) {
      console.error('Error fetching bowl challenge topics:', error);
      throw error;
    }
  }

  async getTopicById(id: string): Promise<Topic | undefined> {
    try {
      const result = await db.select().from(topics).where(eq(topics.id, id));
      return result[0];
    } catch (error) {
      console.error('Error fetching topic by ID:', error);
      throw error;
    }
  }

  async updateTopic(topicId: string, updateData: Partial<Topic>): Promise<Topic | undefined> {
    try {
      const result = await db.update(topics)
        .set(updateData)
        .where(eq(topics.id, topicId))
        .returning();
      return result[0];
    } catch (error) {
      console.error('Error updating topic:', error);
      throw error;
    }
  }

  async createTopic(topicData: any): Promise<Topic> {
    try {
      const result = await db.insert(topics).values(topicData).returning();
      return result[0];
    } catch (error) {
      console.error('Error creating topic:', error);
      throw error;
    }
  }
}
